const ID = 'logonlabs-ui';

var addButtons = function(id, providers, options) {
    var pass = options.pass ? options.pass : false;
    var theme = options.theme ? options.theme : false;
    var element = document.getElementById(id);
    var container = document.createElement('div');
    container.id = ID;
    if (theme == 'icon') {
        container.className = 'icon';
    }
    if (providers) {
        if (providers.length) {
            for (let i = 0; i < providers.length; i++) {
                container.appendChild(buttons(providers[i], theme, pass));
            }
        } else {
            container.appendChild(messages('Need to enable at least 1 identity providers.'));
            container.className += 'error';
        }
    } else {
        container.appendChild(messages('Need to add ' + window.location.origin + ' to CORS whitelist.'));
        container.className += 'error';
    }

    // container.appendChild(footer());
    element.appendChild(container);
};

var messages = function(msg) {
    var div = document.createElement('div');
    div.className = 'message';
    div.innerHTML = msg;
    return div;
};

var footer = function() {
    var ul = document.createElement('ul');
    ul.className = 'footer';
    var lists = [{
        class: 'key',
        text: 'We keep it private'
    },{
        class: 'shield',
        text: 'Share only with permission'
    },{
        class: 'clock',
        text: 'Quick sign in - no passwords'
    }];
    for(var i = 0; i < lists.length; i++) {
        var li = document.createElement('li');
        var span = document.createElement('span');
        span.innerHTML = lists[i].text;
        var icon = document.createElement('span');
        icon.innerHTML = svgFooters(lists[i].class);

        li.appendChild(icon);
        li.appendChild(span);
        ul.appendChild(li);
    }
    return ul;
};

var buttons = function(provider, theme, pass) {
    var div = document.createElement('div');
    if (theme == 'icon') {
        div.innerHTML = svgIcons(provider.type);
    } else {
        div.innerHTML = svgButtons(provider.type);
    }
    var name = document.createAttribute('name');
    name.value = provider.type;
    div.setAttributeNode(name);
    if (!pass) {
        div.onclick = trigger;
    }
    // var node = document.createTextNode(provider.name);
    // div.appendChild(node);
    return div;
};

var trigger = function(e) {
    var name = e.currentTarget.getAttribute("name");
    if (name) {
        var callback = function(url) {
          console.log(url);
        };
        LogonClient.startLogin({
            identity_provider: name
        }, callback);
    }
};

